import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpenseTable from './ExpenseTable';
import ExpenseDetails from './ExpenseDetails';
import ExpenseForm from './ExpenseForm';
import Navbar from './Navbar';
import Reports from './Reports';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [expenses, setExpenses] = useState([]);
    const [incomeAmt, setIncomeAmt] = useState(0);
    const [expenseAmt, setExpenseAmt] = useState(0);
    const [isAdmin, setIsAdmin] = useState(false); // New state to track if user is admin
    const navigate = useNavigate();

    // Define fetchExpenses at the beginning of the component
    const fetchExpenses = useCallback(async () => {
        try {
            const endpoint = isAdmin ? `${APIUrl}/expenses/admin/all` : `${APIUrl}/expenses`;
            const response = await fetch(endpoint, {
                headers: { 'Authorization': localStorage.getItem('token') }
            });
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    }, [navigate, isAdmin]);

    useEffect(() => {
        const userInfo = localStorage.getItem('loggedInUser');
        setLoggedInUser(userInfo);
        setIsAdmin(localStorage.getItem('isAdmin') === 'true'); // Set admin status based on localStorage
        fetchExpenses();
    }, [fetchExpenses]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        localStorage.removeItem('isAdmin');
        handleSuccess('Logged out successfully');
        setTimeout(() => navigate('/login'), 1000);
    };

    useEffect(() => {
        const amounts = expenses.map(item => item.amount);
        const income = amounts.filter(item => item > 0).reduce((acc, item) => acc + item, 0);
        const exp = amounts.filter(item => item < 0).reduce((acc, item) => acc + item, 0) * -1;
        setIncomeAmt(income);
        setExpenseAmt(exp);
    }, [expenses]);

    const deleteExpens = async (id) => {
        try {
            const response = await fetch(`${APIUrl}/expenses/${id}`, {
                method: "DELETE",
                headers: { 'Authorization': localStorage.getItem('token') }
            });
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    const addTransaction = async (data) => {
        try {
            const response = await fetch(`${APIUrl}/expenses`, {
                method: "POST",
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            });
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    return (
        <div className='home-container'>
            <Navbar onLogout={handleLogout} />
            <div className='user-section'>
                <h1>Welcome, {loggedInUser}</h1>
                <button className='logout-button' onClick={handleLogout}>Logout</button>
            </div>
            <div className='content-grid'>
                <ExpenseDetails incomeAmt={incomeAmt} expenseAmt={expenseAmt} />
                <ExpenseForm addTransaction={addTransaction} />
            </div>
            <ExpenseTable expenses={expenses} deleteExpens={deleteExpens} isAdmin={isAdmin} />
            <Reports expenses={expenses} fetchExpenses={fetchExpenses} />
            <ToastContainer />
        </div>
    );
}

export default Home;
