// src/pages/Reports.js
import React from 'react';
import { Bar, Pie, Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement, PointElement, LineElement } from 'chart.js';

// Register the chart components
ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
    PointElement,
    LineElement
);

const Reports = () => {
    // Sample data for the Bar Chart
    const barData = {
        labels: ['January', 'February', 'March', 'April', 'May', 'June'],
        datasets: [
            {
                label: 'Expenses',
                data: [300, 200, 400, 500, 300, 600],
                backgroundColor: 'rgba(75, 192, 192, 0.5)',
            },
        ],
    };

    const barOptions = {
        responsive: true,
        plugins: {
            legend: { position: 'top' },
            title: { display: true, text: 'Monthly Expenses' },
        },
    };

    // Sample data for the Pie Chart
    const pieData = {
        labels: ['Food', 'Transport', 'Entertainment', 'Utilities', 'Healthcare'],
        datasets: [
            {
                label: 'Category-wise Expenses',
                data: [30, 20, 15, 25, 10],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.6)',
                    'rgba(54, 162, 235, 0.6)',
                    'rgba(255, 206, 86, 0.6)',
                    'rgba(75, 192, 192, 0.6)',
                    'rgba(153, 102, 255, 0.6)',
                ],
            },
        ],
    };

    // Sample data for the Line Chart
    const lineData = {
        labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
        datasets: [
            {
                label: 'Weekly Expenses Trend',
                data: [100, 150, 200, 170],
                borderColor: 'rgba(75, 192, 192, 1)',
                backgroundColor: 'rgba(75, 192, 192, 0.3)',
                fill: true,
            },
        ],
    };

    const lineOptions = {
        responsive: true,
        plugins: {
            legend: { position: 'top' },
            title: { display: true, text: 'Weekly Expenses Trend' },
        },
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Reports</h1>

            <div style={{ maxWidth: '700px', margin: 'auto', padding: '20px' }}>
                <h3>Monthly Expenses (Bar Chart)</h3>
                <Bar data={barData} options={barOptions} />
            </div>

            <div style={{ maxWidth: '500px', margin: 'auto', padding: '20px' }}>
                <h3>Category-wise Expenses (Pie Chart)</h3>
                <Pie data={pieData} />
            </div>

            <div style={{ maxWidth: '700px', margin: 'auto', padding: '20px' }}>
                <h3>Weekly Expenses Trend (Line Chart)</h3>
                <Line data={lineData} options={lineOptions} />
            </div>
        </div>
    );
};

export default Reports;
